from selenium import webdriverfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.common.keys import Keysfrom selenium.webdriver.support.ui import WebDriverWaitfrom selenium.webdriver.support import expected_conditions as ECfrom time import sleep# URL of the siteurl = "https://www.irctc.co.in/nget/train-search"def getBrowser():    options = webdriver.ChromeOptions()    options.use_chromium = True        # options.add_argument('--headless')  # Run Edge in headless mode    return webdriver.Chrome(options=options)  # Function to perform movie searchdef searchTrain(station_from,station_to):        try:        # Open the  website       browser = getBrowser()       print("Browser completed")       browser.get(url)             des_from=browser.find_element(By.XPATH,"//body/app-root[1]/app-home[1]/div[3]/div[1]/app-main-page[1]/div[1]/div[1]/div[1]/div[1]/div[1]/app-jp-input[1]/div[1]/form[1]/div[2]/div[1]/div[1]/p-autocomplete[1]/span[1]/input[1]")       des_from.send_keys(station_from)       des_from.send_keys(Keys.RETURN)              des_to=browser.find_element(By.XPATH,"/html/body/app-root/app-home/div[3]/div/app-main-page/div/div/div[1]/div[1]/div[1]/app-jp-input/div/form/div[2]/div[1]/div[2]/p-autocomplete/span/input")       des_to.send_keys(station_to)              des_to.send_keys(Keys.RETURN)       sleep(5)           except Exception as e:        print(f"An error occurred: {e}")    finally:        # Close the WebDriver        browser.quit()# Example usagestation_from="ROURKELA - ROU (ROURKELA)"station_to="SURAT - ST"searchTrain(station_from,station_to);